var link=document.querySelector(".btn-contacts"),popup=document.querySelector(".order-content"),close=popup.querySelector(".order-close"),form=popup.querySelector("form"),username=popup.querySelector("[name=username]"),email=popup.querySelector("[name=email]"),storage=localStorage.getItem("username");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("order-show"),storage?(username.value=storage,email.focus()):username.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("order-show")}),form.addEventListener("submit",function(e){username.value&&email.value?localStorage.setItem("username",username.value):(e.preventDefault(),console.log("Нужно ввести имя и e-mail"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("order-show")&&popup.classList.remove("order-show")});